mapper.select:
select *
from
student


		student_num = #{student_num}



		and student_name like #{student_name}



		and branch_name like {branch_name}

	and student_status=#{student_status}

select *
from
student_view



		and student_name like #{student_name}



		and branch_name like #{branch_name}

	and student_status = #{student_status}
	and student_num = #{student_num}

mapper.update:
update student

	student_tel =#{student_tel},
	student_email = #{student_email}

where student_id =
#{student_id}

update student
student_name = #{student_name}, student_gender = #{student_gender}, student_tel = #{student_tel}, student_email = #{student_email}
where student_num = #{student_num}

update student
set branch_id = #{branch_id}
where
student_num = #{student_num}

update student
set student_permission =
#{student_permission}
where student_num = #{student_num}

update student
set student_status = student_status + 1
where student_num = #{student_num}
